import Header from '../components/header'
import axios from 'axios'

function BlogPage({blogs,error}) {
    return (
        <div>
            <Header />
            <h1>博客页面</h1>
            <ul>{blogs?blogs.map((blog,index)=>{
                return (
                    <li key={blog._id}>
                        <h1>{blog.title}</h1>
                    </li>
                )
            }):function(){
                return (
                    <p>failed:{JSON.stringify(error)}</p>
                )
            }()}
            </ul>
        </div>
    )
}

