import {useRouter} from 'next/router'
import axios from 'axios'

function BlogDetail({blog}) {

    return (
        <div>
            <h1>{blog.title}</h1>
            <p>{blog.author}</p>
            <p>{blog.date}</p>
        </div>
    )
}

export async function getStaticProps() {
    const router = useRouter()

    const id = router.query.id
    console.log(id)
    let blog = null
    await axios.get(`http://localhost:8000/blogs/detail?${id}`)
    .then((res)=>{
        blog = res.data
    })
    .catch((error)=>{
        console.log(error)
    })

    return {
        props:{
            blog
        }
    }
}

export default BlogDetail
