import Header from '../components/header'
import axios from 'axios'

function BlogPage({data,error}) {
    return (
        <div>
            <Header />
            <h1>博客页面</h1>
            <ul>{data?data.map((blog,index)=>{
                return (
                    <li key={blog._id}>
                        <h1>{blog.title}</h1>
                    </li>
                )
            }):function(){
                return (
                    <p>failed:{JSON.stringify(err)}</p>
                )
            }()}
            </ul>
        </div>
    )
}

export async function getStaticProps(context) {
    let data = null
    let err = null
    await axios.get('http://127.0.0.1:8000/blogs/list')
        .then((res) => {
                console.log(res.data)
                data=res.data
            // return {
            //     props:{
            //         blogs:res.data
            //     }
            // }
        })
        .catch((error)=>{
            err = error
        })

        return {
            props:{data,err}
        }

        if (data) {
            return {
                props:{
                    blogs:data

                }
            }
        }
        return {
            props:{
                data:[{}],
                err:err
            }
        }

}

export default BlogPage
