import Header from '../components/header'
import axios from 'axios'

function BlogPage(props) {
    return (
        <div>
            <Header />
            <h1>博客页面</h1>
            <ul>{props.blogs.map(blog => {
                    return(
                        <li key={blog._id}>
                    <h1>{blog.title}</h1>
                </li>
                    )
                })}
            </ul>
        </div>
    )
}

BlogPage.getInitialProps = async () => {
    const data = await axios.get('http://127.0.0.1:8000/blogs/list')
        .then((res) => {
            return res.data
        })

    return {
        props: {
            blogs:data
        }
    }

}

export default BlogPage
