import React, { Component } from 'react'
import axios  from 'axios'

export default class Add extends Component{
    constructor(props){
        super(props)
        this.state = {
            title:null,
            content:null
        }

    }

    saveArticle = () => {
        if(this.state.title === null){
            console.error('标题为空')
            return false
        }
        if(this.state.content === null){
            console.error('内容为空')
            return false
        }
    }


    render(){
        <div>
            <h1>添加博客</h1>
            <h3>标题</h3>
            <input type="text"/>
            <h3>内容</h3>
            <textarea /> 
            <input type="button" onClick={saveArticle}/>
        </div>
    }
}
