import Header from '../components/header'
import axios from 'axios'

function BlogPage({blogs}) {
    return (
        <div>
            <Header />
            <h1>博客页面</h1>
            <ul>{blogs.map((blog, index) => {
                if (!blog) {
                    return (
                        <h1>no data!</h1>
                    )
                }
                return (
                    <li key={blog._id}>
                        <h1>{blog.title}</h1>
                    </li>
                )
            })}
            </ul>
        </div>
    )
}

BlogPage.getInitialProps = async () => {
    let data = null
    let err = null
    await axios.get('http://127.0.0.1:8000/blogs/list')
        .then((res) => {
            data= res.data
        })
        .catch((error)=>{
            err = error
        })

        if (data) {
            return {
                props:{
                    blogs:data

                }
            }
        }
        return {
            props:{
                data:[{}],
                err:err
            }
        }

}

export default BlogPage
