import Header from '../components/header'
import axios from 'axios'
import Blog from '../../server/model/blog_model'

function Blogs(props) {
    return (
        <div>
            <Header />
            <h1>博客页面</h1>
            <ul>
                props.data.map((result)=>{
                    <li key={result.id}>
                        <h1>{result.title}</h1>
                    </li>
                })
            </ul>
        </div>
    )
}

Blogs.getInitialProps = async () => {
    const result = axios.get('http://localhost:8000/blogs/list')
    .then((res)=>{
        console.log('success')
        console.log(res)
        return res
    })
    .catch((err)=>{
        console.log(err)
        return {}
    })

    return {
        data:result
    }
}

export default Blogs
