import Header from '../components/header'
import axios from 'axios'

function BlogPage({blogs}) {
    return (
        <div>
            <Header />
            <h1>博客页面</h1>
            <ul>{blogs?blogs.map((blog,index)=>{
                return (
                    <li key={blog._id}>
                        <h1>{blog.title}</h1>
                    </li>
                )
            }):function(){
                return (
                    <p>failed:{JSON.stringify(blogs)}</p>
                )
            }()}
            </ul>
        </div>
    )
}

export async function getInitialProps(context) {
    let data = null
    let err = null
    return await axios.get('http://127.0.0.1:8000/blogs/list')
        .then((res) => {
                console.log(res.data)
            return {
                props:{
                    blogs:res.data
                }
            }
        })
        .catch((err)=>{
            console.log(err)
            return {
                props:{
                    blogs:null
                }
            }
        })
        console.log(data)

        if (data) {
            return {
                props:{
                    blogs:data

                }
            }
        }
        return {
            props:{
                data:[{}],
                err:err
            }
        }

}

export default BlogPage
